mapper.delete:
DELETE FROM SCHEDULE 
WHERE SCHEDULE_NO = #{scheduleNo}
AND MEMBER_NO = #{memberNo}

mapper.select:
UPDATE FILES SET
    FILE_PATH = #{filePath},
    FILE_RENAME = #{fileReName},
    FILE_ORIGINAL = #{fileOriginal}
WHERE TARGET_NO = #{targetNo}

mapper.delete:
DELETE FROM FILES
   WHERE TARGET_NO = #{scheduleNo}
   AND FILE_CODE = 4
